mapper.select:
select count(*)
from member
where te_id=#{teId}

select count(*)
from member
where te_email=#{teEmail}

mapper.update:
update emailchk
set te_email_key=#{teEmailKey}
where te_email=#{teEmail}

mapper.select:
select te_email
from emailchk
where te_email=#{teEmail} and te_email_key=#{teEmailKey}

mapper.update:
update emailchk
set te_email_auth=1
where te_email=#{teEmail} and te_email_key=#{teEmailKey}

mapper.select:
select te_email_auth
from emailchk
where te_email=#{teEmail} and te_email_key=#{teEmailKey}

select te_id, te_pwd, te_name, te_profile, te_email
from member
where te_id=#{teId} and te_pwd=#{tePwd}

select te_id
from member
where te_email=#{teEmail}

select count(*)
from member
where te_id=#{teId} and te_email=#{teEmail}

mapper.update:
update member
set te_pwd=#{tePwd}
where te_email=#{teEmail}

mapper.select:
select te_id, te_name, te_profile, te_profilename, te_name, te_email, te_phonenum, te_birth, te_prefertrip
from member
where te_id=#{teId}

mapper.update:
update member
set te_profile=#{teProfile}, te_profilename=#{teProfileName}, te_phonenum=#{tePhoneNum}, te_prefertrip=#{tePreferTrip}
where te_Id=#{teId}

update member
set te_pwd=#{tePwd}
where te_id=#{teId}
